// src/redux/cartActions.js

// Add to Cart



import axios from 'axios';
import { ADD_TO_CART, GET_CART, CART_ERROR } from './types';


// Add product to cart
export const addToCart = (product) => async (dispatch, getState) => {
  try {
    const { token } = getState().user; // Get the user's token from the Redux state

    const config = {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}` // Add the token to the Authorization header
      }
    };

    const res = await axios.post('http://localhost:4000/cart/addtocart', {
      itemId: product._id
    }, config);

    dispatch({
      type: ADD_TO_CART,
      payload: res.data
    });
  } catch (error) {
    dispatch({
      type: CART_ERROR,
      payload: error.response?.data || 'An error occurred'
    });
  }
};

// Get cart items
export const getCartItems = () => async (dispatch, getState) => {
  try {
    const { token } = getState().user; // Get the token from Redux state

    const config = {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    };

    const res = await axios.get('http://localhost:4000/cart/getcart', config);

    dispatch({
      type: GET_CART,
      payload: res.data
    });
  } catch (error) {
    dispatch({
      type: CART_ERROR,
      payload: error.response?.data || 'An error occurred'
    });
  }
};


export const removeFromCart = (itemId) => async dispatch => {
  try {
    const res = await axios.delete(`${API_URL}/removefromcart`, { data: { itemId } });
    dispatch({
      type: REMOVE_FROM_CART,
      payload: itemId
    });
  } catch (error) {
    dispatch({
      type: CART_ERROR,
      payload: error.response.data
    });
  }
};
