import React from 'react';
import './ProductDisplay.css';

const ProductDisplay = ({ product }) => {
  console.log(product); // Debugging line

  if (!product) {
    return <div>Product not found</div>;
  }

  // Assuming 'image' field contains the relative path
  const imageUrl = product.image.startsWith('/') ? `http://localhost:4000${product.image}` : product.image;

  return (
    <div className='productdisplay'>
      <div className="productdisplay-left">
        <div className="productdisplay-img-list">
          <img src={imageUrl} alt="Product" />
          {/* Add other images if available */}
        </div>
        <div className="productdisplay-img">
          <img className='productdisplay-main-img' src={imageUrl} alt={product.name} />
        </div>
      </div>
      <div className="productdisplay-right">
        <h1>{product.name}</h1>
        <div className="productdisplay-right-stars">
          {/* Star icons */}
        </div>
        <div className="productdisplay-right-prices">
          <div className="productdisplay-right-price-old">${product.old_price}</div>
          <div className="productdisplay-right-price-new">${product.new_price}</div>
        </div>
        <div className="productdisplay-right-description">
          {/* Product description */}
        </div>
        <div className="productdisplay-right-size">
          <h1>Select Size</h1>
          <div className="productdisplay-right-sizes">
            <div>S</div>
            <div>M</div>
            <div>L</div>
            <div>XL</div>
            <div>XXL</div>
          </div>
        </div>
        <button>Add to Cart</button>
        <p className='productdisplay-right-category'><span>Category :</span>{product.category}</p>
        <p className='productdisplay-right-category'><span>Tags :</span>Modern, Latest</p>
      </div>
    </div>
  );
};

