import React, { createContext, useState, useEffect } from "react";
import axios from 'axios';

// Define getDefaultCart function
const getDefaultCart = (products) => {
  let cart = {};
  products.forEach(product => {
    cart[product._id] = 0;
  });
  return cart;
};

export const ShopContext = createContext(null);

const ShopContextProvider = (props) => {
  const [cartItems, setCartItems] = useState({});
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:4000/allproducts');
        const productsData = response.data;
        setProducts(productsData);
        setCartItems(getDefaultCart(productsData)); // Set default cart after fetching products
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, []);

  const addToCart = (itemId) => {
    setCartItems((prev) => ({ ...prev, [itemId]: (prev[itemId] || 0) + 1 }));
  };

  const removeFromCart = (itemId) => {
    setCartItems((prev) => {
      if (prev[itemId] <= 1) {
        const { [itemId]: _, ...rest } = prev; // Remove item if quantity is 0 or less
        return rest;
      }
      return { ...prev, [itemId]: (prev[itemId] || 0) - 1 };
    });
  };

  const getTotalCartAmount = () => {
    return Object.keys(cartItems).reduce((totalAmount, item) => {
      const itemInfo = products.find((product) => product._id === item);
      if (itemInfo && cartItems[item] > 0) {
        totalAmount += cartItems[item] * itemInfo.new_price;
      }
      return totalAmount;
    }, 0);
  };

  const getTotalCartItems = () => {
    return Object.values(cartItems).reduce((totalItem, quantity) => totalItem + (quantity > 0 ? quantity : 0), 0);
  };

  const contextValue = {
    getTotalCartItems,
    getTotalCartAmount,
    products,
    cartItems,
    addToCart,
    removeFromCart
  };

  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
