// src/redux/types/cartTypes.js
export const FETCH_CART_ITEMS = 'FETCH_CART_ITEMS';
export const ADD_TO_CART = 'ADD_TO_CART';
export const REMOVE_FROM_CART = 'REMOVE_FROM_CART';
export const UPDATE_CART_QUANTITY = 'UPDATE_CART_QUANTITY';

// src/redux/actions/cartActions.js
import axios from 'axios';

export const fetchCartItems = (userId) => async (dispatch) => {
  try {
    const response = await axios.get(`/api/getcart/${userId}`);
    dispatch({
      type: FETCH_CART_ITEMS,
      payload: response.data,
    });
  } catch (error) {
    console.error('Error fetching cart items:', error);
  }
};

export const addToCart = (userId, productId) => async (dispatch) => {
  try {
    await axios.post('/api/addtocart', { userId, itemId: productId });
    dispatch({
      type: ADD_TO_CART,
      payload: { productId },
    });
  } catch (error) {
    console.error('Error adding to cart:', error);
  }
};

export const removeFromCart = (userId, productId) => async (dispatch) => {
  try {
    await axios.post('/api/removefromcart', { userId, itemId: productId });
    dispatch({
      type: REMOVE_FROM_CART,
      payload: { productId },
    });
  } catch (error) {
    console.error('Error removing from cart:', error);
  }
};

export const updateCartQuantity = (userId, productId, quantity) => async (dispatch) => {
  try {
    await axios.post('/api/updatecartquantity', { userId, itemId: productId, quantity });
    dispatch({
      type: UPDATE_CART_QUANTITY,
      payload: { productId, quantity },
    });
  } catch (error) {
    console.error('Error updating cart quantity:', error);
  }
};
