import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom'; // Import useNavigate for redirection
import ReCAPTCHA from 'react-google-recaptcha';
import './CSS/LoginSignup.css';

const LoginSignup = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });
  const [captchaToken, setCaptchaToken] = useState('');
  const [error, setError] = useState('');
  const [state, setState] = useState('Login'); // States: 'Login', 'Sign Up', 'Forgot Password'
  const navigate = useNavigate(); // Use navigate for redirection

  const changeHandler = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleCaptcha = (value) => {
    setCaptchaToken(value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
  
    if (!formData.email || (state !== 'Forgot Password' && !formData.password)) {
      setError('Please fill all fields');
      return;
    }
  
    if (!captchaToken) {
      setError('Please complete the CAPTCHA');
      return;
    }
  
    try {
      let endpoint;
      let response;
  
      if (state === 'Forgot Password') {
        endpoint = '/auth/forgot-password';
        await axios.post(endpoint, {
          email: formData.email,
          captchaToken
        });
        alert('Password reset email sent if the email exists.');
      } else {
        endpoint = state === 'Login' ? '/auth/login' : '/auth/signup';
        response = await axios.post(endpoint, {
          ...formData,
          captchaToken
        });
  
        if (response.data && response.data.token) {
          localStorage.setItem('token', response.data.token);
          navigate('/home');
        } else {
          setError('Unexpected response from server. Token not received.');
        }
      }
    } catch (err) {
      console.error('Error details:', err);
  
      // Check if there is a response object
      if (err.response) {
        console.error('Error response:', err.response);
        
        // Check if there is response data
        if (err.response.data) {
          console.error('Error response data:', err.response.data);
          
          // Check if there are errors in response data
          if (err.response.data.errors) {
            setError(err.response.data.errors);
          } else {
            setError('An unexpected error occurred');
          }
        } else {
          setError('An unexpected error occurred without response data');
        }
      } else {
        setError('An unexpected error occurred without response object');
      }
    }
  };
  
  
  return (
    <div className="loginsignup">
      <div className="loginsignup-container">
        <h1>{state}</h1>
        <form onSubmit={handleSubmit}>
          <div className="loginsignup-fields">
            {state === "Sign Up" && (
              <input
                type="text"
                placeholder="Your Name"
                name="username"
                value={formData.username}
                onChange={changeHandler}
              />
            )}
            <input
              type="email"
              placeholder="Email Address"
              name="email"
              value={formData.email}
              onChange={changeHandler}
            />
            {state !== "Forgot Password" && (
              <input
                type="password"
                placeholder="Password"
                name="password"
                value={formData.password}
                onChange={changeHandler}
              />
            )}
          </div>

          <ReCAPTCHA
            sitekey="6LeSAz0qAAAAAB-Bq9xTnXnQDi0W5rZnfAzr3D0l" // Replace with your reCAPTCHA site key
            onChange={handleCaptcha}
          />

          <button type="submit">Continue</button>

          {state === "Login" ? (
            <>
              <p className="loginsignup-login">
                Don't have an account? <span onClick={() => setState("Sign Up")}>Sign up here</span>
              </p>
              <p className="loginsignup-login">
                Forgot your password? <span onClick={() => setState("Forgot Password")}>Click here</span>
              </p>
            </>
          ) : state === "Sign Up" ? (
            <p className="loginsignup-login">
              Already have an account? <span onClick={() => setState("Login")}>Login here</span>
            </p>
          ) : (
            <p className="loginsignup-login">
              Remembered your password? <span onClick={() => setState("Login")}>Login here</span>
            </p>
          )}

          <div className="loginsignup-agree">
            <input type="checkbox" name="agree" id="agree" />
            <p>By continuing, I agree to the terms of use & privacy policy.</p>
          </div>

          {error && <p className="loginsignup-error">{error}</p>}
        </form>
      </div>
    </div>
  );
};

export default LoginSignup;
