import React, { useState } from 'react';
import axios from 'axios';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import ReCAPTCHA from 'react-google-recaptcha';
import {
  loginSuccess,
  loginFail,
  signupSuccess,
  signupFail,
  forgotPasswordSuccess,
  forgotPasswordFail
} from '../redux/actions/authActions'; // Ensure correct path and action names
import './CSS/LoginSignup.css';

const LoginSignup = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });
  const [captchaToken, setCaptchaToken] = useState('');
  const [error, setError] = useState('');
  const [state, setState] = useState('Login');
  const navigate = useNavigate();
  const dispatch = useDispatch();
  const authState = useSelector((state) => state.auth);

  const changeHandler = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleCaptcha = (value) => {
    setCaptchaToken(value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.email || (state !== 'Forgot Password' && !formData.password)) {
      setError('Please fill all fields');
      return;
    }

    if (!captchaToken) {
      setError('Please complete the CAPTCHA');
      return;
    }

    try {
      let endpoint;
      let response;

      if (state === 'Forgot Password') {
        endpoint = '/auth/forgot-password';
        response = await axios.post(endpoint, {
          email: formData.email,
          captchaToken
        });
        dispatch(forgotPasswordSuccess(response.data.message));
        alert('Password reset email sent if the email exists.');
      } else {
        endpoint = state === 'Login' ? '/auth/login' : '/auth/signup';
        response = await axios.post(endpoint, {
          ...formData,
          captchaToken
        });

        if (response.data && response.data.token) {
          localStorage.setItem('token', response.data.token);
          dispatch(state === 'Login' ? loginSuccess(response.data.token) : signupSuccess(response.data.token));
          navigate('/home');
        } else {
          dispatch(state === 'Login' ? loginFail('Unexpected response from server. Token not received.') : signupFail('Unexpected response from server. Token not received.'));
        }
      }
    } catch (err) {
      console.error('Error details:', err);

      let errorMsg = 'An unexpected error occurred';
      if (err.response && err.response.data && err.response.data.errors) {
        errorMsg = err.response.data.errors;
      }

      setError(errorMsg);
      dispatch(state === 'Login' ? loginFail(errorMsg) : signupFail(errorMsg));
    }
  };

  return (
    <div className="loginsignup">
      <div className="loginsignup-container">
        <h1>{state}</h1>
        <form onSubmit={handleSubmit}>
          <div className="loginsignup-fields">
            {state === 'Sign Up' && (
              <input
                type="text"
                placeholder="Your Name"
                name="username"
                value={formData.username}
                onChange={changeHandler}
              />
            )}
            <input
              type="email"
              placeholder="Email Address"
              name="email"
              value={formData.email}
              onChange={changeHandler}
            />
            {state !== 'Forgot Password' && (
              <input
                type="password"
                placeholder="Password"
                name="password"
                value={formData.password}
                onChange={changeHandler}
              />
            )}
          </div>

          <ReCAPTCHA
            sitekey="6LeSAz0qAAAAAB-Bq9xTnXnQDi0W5rZnfAzr3D0l"
            onChange={handleCaptcha}
          />

          <div className="loginsignup-agree">
            <input type="checkbox" name="agree" id="agree" />
            <p>By continuing, I agree to the terms of use & privacy policy.</p>
          </div>
          <button type="submit">Continue</button>

          {state === 'Login' ? (
            <>
              <p className="loginsignup-login">
                Don't have an account? <span onClick={() => setState('Sign Up')}>Sign up here</span>
              </p>
              <p className="loginsignup-login">
                Forgot your password? <span onClick={() => setState('Forgot Password')}>Click here</span>
              </p>
            </>
          ) : state === 'Sign Up' ? (
            <p className="loginsignup-login">
              Already have an account? <span onClick={() => setState('Login')}>Login here</span>
            </p>
          ) : (
            <p className="loginsignup-login">
              Remembered your password? <span onClick={() => setState('Login')}>Login here</span>
            </p>
          )}

          {error && <p className="loginsignup-error">{error}</p>}
        </form>
      </div>
    </div>
  );
};

export default LoginSignup;
