// // src/components/CartItems.js
import React, { useEffect } from "react";
import "./CartItems.css";
import cross_icon from "../Assets/cart_cross_icon.png";
import { useSelector, useDispatch } from "react-redux";
// import { backend_url, currency } from "../../App";


import { getCart, updateCartQuantity, removeFromCart } from '../../redux/actions/cartActions';

const CartItems = () => {
  const dispatch = useDispatch();
  const cartItems = useSelector(state => state.cart.items);
  const error = useSelector(state => state.cart.error);
  const loading = useSelector(state => state.cart.loading);

  useEffect(() => {
    dispatch(getCart());
  }, [dispatch]);

  const handleIncrease = (itemId) => {
    const item = cartItems.find(item => item._id === itemId);
    if (item) {
      dispatch(updateCartQuantity(itemId, item.quantity + 1));
    }
  };

  const handleDecrease = (itemId) => {
    const item = cartItems.find(item => item._id === itemId);
    if (item && item.quantity > 1) {
      dispatch(updateCartQuantity(itemId, item.quantity - 1));
    }
  };

  const handleRemove = (itemId) => {
    dispatch(removeFromCart(itemId));
  };
  if (loading) {
    return <div>Loading cart items...</div>;
  }

  if (error) {
    return (
      <div>
        <p>Error loading cart: {error}</p>
        <button onClick={() => dispatch(getCart())}>Try Again</button>
      </div>
    );
  }

  return (
    // <div className="cart-container">
    //   <h1>Your Cart</h1>
    //   {cartItems.length === 0 ? (
    //     <p>Your cart is empty</p>
    //   ) : (
    //     <ul className="cart-list">
    //       {cartItems.map(item => (
    //         <li key={item._id} className="cart-item">
    //           <img src={`http://localhost:4000${item.image}`} alt={item.name} className="cart-item-image" />
    //           <div className="cart-item-details">
    //             <h2>{item.name}</h2>
    //             <p>Price: ${item.new_price}</p>
    //             <p>Quantity: {item.quantity}</p>
    //             <button onClick={() => handleDecrease(item._id)} className="quantity-button">-</button>
    //             <button onClick={() => handleIncrease(item._id)} className="quantity-button">+</button>
    //             <button onClick={() => handleRemove(item._id)} className="remove-button">
    //               Remove
    //             </button>
    //           </div>
    //         </li>
    //       ))}
    //     </ul>
    //   )}
    //   {cartItems.length > 0 && (
    //     <div className="cart-total">
    //       <h2>Total: ${cartItems.reduce((total, item) => total + item.new_price * item.quantity, 0).toFixed(2)}</h2>
    //     </div>
    //   )}
    // </div>
    <div className="cartitems">
    <div className="cartitems-format-main">
      <p>Products</p>
      <p>Title</p>
      <p>Price</p>
      <p>Quantity</p>
      <p>Total</p>
      <p>Remove</p>
    </div>
    <hr />
    {produ.map((e) => {
      if (cartItems[e.id] > 0) {
        return (
          <div key={e.id}>
            <div className="cartitems-format-main cartitems-format">
              <img className="cartitems-product-icon" src={`http://localhost:4000${e.image}`} alt={e.name} />
              <p className="cartitems-product-title">{e.name}</p>
              <p>{currency}{e.new_price}</p>
              <div className="cartitems-quantity-container">
                <button onClick={() => handleDecrease(e.id)} className="cartitems-quantity-button">-</button>
                <span className="cartitems-quantity">{cartItems[e.id]}</span>
                <button onClick={() => handleIncrease(e.id)} className="cartitems-quantity-button">+</button>
              </div>
              <p>{currency}{(e.new_price * cartItems[e.id]).toFixed(2)}</p>
              <img onClick={() => handleRemove(e.id)} className="cartitems-remove-icon" src={cross_icon} alt="Remove" />
            </div>
            <hr />
          </div>
        );
      }
      return null;
    })}

    {cartItems.length > 0 && (
      <div className="cartitems-down">
        <div className="cartitems-total">
          <h1>Cart Totals</h1>
          <div>
            <div className="cartitems-total-item">
              <p>Subtotal</p>
              <p>{currency}{getTotalCartAmount()}</p>
            </div>
            <hr />
            <div className="cartitems-total-item">
              <p>Shipping Fee</p>
              <p>Free</p>
            </div>
            <hr />
            <div className="cartitems-total-item">
              <h3>Total</h3>
              <h3>{currency}{getTotalCartAmount()}</h3>
            </div>
          </div>
          <button>PROCEED TO CHECKOUT</button>
        </div>

        <div className="cartitems-promocode">
          <p>If you have a promo code, Enter it here</p>
          <div className="cartitems-promobox">
            <input type="text" placeholder="promo code" />
            <button>Submit</button>
          </div>
        </div>
      </div>
    )}
  </div>
);
};
  


export default CartItems;
