// src/store.js
import { createStore, combineReducers } from 'redux';
import { Provider } from 'react-redux';
import cartReducer from './reducers/cartReducer'; // Create this reducer based on your cart logic
import productReducer from './reducers/productReducer'; // Create this reducer based on your products logic

const rootReducer = combineReducers({
  cart: cartReducer,
  products: productReducer,
  // Add other reducers here
});

const store = createStore(rootReducer);

export default store;
