import {
  ADD_TO_CART_SUCCESS,
  ADD_TO_CART_FAIL,
  REMOVE_FROM_CART_SUCCESS,
  REMOVE_FROM_CART_FAIL,
  UPDATE_CART_SUCCESS,
  UPDATE_CART_FAIL,
  GET_CART_REQUEST,
  GET_CART_SUCCESS,
  GET_CART_FAIL,
  CART_ERROR
} from '../';

const initialState = {
  items: [],
  loading: false,
  error: null
};

const cartReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_CART_REQUEST:
      return {
        ...state,
        loading: true
      };
    case GET_CART_SUCCESS:
      return {
        ...state,
        items: action.payload,
        loading: false
      };
    case GET_CART_FAIL:
      return {
        ...state,
        error: action.payload,
        loading: false
      };
    case ADD_TO_CART_SUCCESS:
    case REMOVE_FROM_CART_SUCCESS:
    case UPDATE_CART_SUCCESS:
      return {
        ...state,
        items: action.payload,
        loading: false
      };
    case ADD_TO_CART_FAIL:
    case REMOVE_FROM_CART_FAIL:
    case UPDATE_CART_FAIL:
      return {
        ...state,
        error: action.payload,
        loading: false
      };
    case CART_ERROR:
      return {
        ...state,
        error: action.payload
      };
    default:
      return state;
  }
};

export default cartReducer;
