import React, { useEffect, useState } from 'react';
import './RelatedProducts.css';
import axios from 'axios';
import Item from '../Item/Item';

const RelatedProducts = ({ category, currentProductId }) => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get('http://localhost:4000/aproducts');
        const allProducts = response.data;
        
        // Filter products by category and exclude the current product
        const filteredProducts = allProducts
          .filter(product => product.category === category && product._id !== currentProductId)
          .slice(0, 5); // Limit to 5 products
// Inside the fetchProducts function
// const filteredProducts = allProducts
//   .filter(product => product.category === category && product._id !== currentProductId);

// // Shuffle the filtered products
// const shuffled = filteredProducts.sort(() => 0.5 - Math.random());

// // Get sub-array of first 5 elements after shuffling
// const selected = shuffled.slice(0, 5);

// setProducts(selected);
        setProducts(filteredProducts);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };

    fetchProducts();
  }, [category, currentProductId]);

  return (
    <div className='relatedproducts'>
      <h1>Related Products</h1>
      <hr />
      <div className="relatedproducts-item">
        {products.map((item) => (
          <Item
            key={item._id}
            id={item._id}
            name={item.name}
            image={item.image}
            new_price={item.new_price}
            old_price={item.old_price}
          />
        ))}
      </div>
    </div>
  );
}

export default RelatedProducts;
