import React, { useContext } from 'react';
import './ProductDisplay.css';
import star_icon from "../Assets/star_icon.png";
import star_dull_icon from "../Assets/star_dull_icon.png";
import { ShopContext } from '../../Context/ShopContext';

const ProductDisplay = (props) => {
  const { product } = props;
  const { addToCart } = useContext(ShopContext);

  // Check if the product exists
  if (!product) return <p>Loading...</p>; // Return a loading state or message if product is undefined

  // Ensure product properties are valid
  const { images = [], name, old_price, new_price, description = '', sizes = [], category = '', tags = [] } = product;

  return (
    <div className='productdisplay'>
      <div className="productdisplay-left">
        <div className="productdisplay-img-list">
          {images.length > 0 ? images.map((image, index) => (
            <img key={index} src={image} alt={`Product image ${index + 1}`} />
          )) : <p>No images available</p>}
        </div>
        <div className="productdisplay-img">
          {images.length > 0 ? (
            <img className='productdisplay-main-img' src={images[0]} alt="Main product view" />
          ) : (
            <p>No main image available</p>
          )}
        </div>
      </div>
      <div className="productdisplay-right">
        <h1>{name || 'Product Name'}</h1>
        <div className="productdisplay-right-stars">
          {[...Array(5)].map((_, index) => (
            <img
              key={index}
              src={index < 4 ? star_icon : star_dull_icon} // Replace 4 with dynamic rating if available
              alt="Rating star"
            />
          ))}
          <p>(122)</p> {/* Replace with dynamic reviews count if available */}
        </div>
        <div className="productdisplay-right-prices">
          {old_price && (
            <div className="productdisplay-right-price-old">${old_price}</div>
          )}
          <div className="productdisplay-right-price-new">${new_price}</div>
        </div>
        <div className="productdisplay-right-description">
          {description || 'Description not available.'}
        </div>
        <div className="productdisplay-right-size">
          <h1>Select Size</h1>
          <div className="productdisplay-right-sizes">
            {sizes.length > 0 ? sizes.map((size, index) => (
              <div key={index}>{size}</div>
            )) : <p>No sizes available</p>}
          </div>
        </div>
        <button onClick={() => addToCart(product._id)}>ADD TO CART</button>
        <p className='productdisplay-right-category'>
          <span>Category :</span> {category || 'Category not available'}
        </p>
        <p className='productdisplay-right-tags'>
          <span>Tags :</span> {tags.length > 0 ? tags.join(', ') : 'No tags'}
        </p>
      </div>
    </div>
  );
}

export default ProductDisplay;
