// src/redux/cartActions.js

// Add to Cart


import axios from 'axios';

// Action Types
export const GET_CART = 'GET_CART';
export const ADD_TO_CART = 'ADD_TO_CART';
export const REMOVE_FROM_CART = 'REMOVE_FROM_CART';
export const CART_ERROR = 'CART_ERROR';
const API_URL = 'http://localhost:4000/cart';
// Ensure that you're accessing the correct part of the state
const { token } = getState().auth?.user || {}; // Optional chaining and fallback


// Action Creators

// Add product to cart
export const addToCart = (product) => async (dispatch, getState) => {
  try {
    console.log("add to cart started");
    
    // Retrieve the token from Redux state
    const { token } = getState().user; 
    if (!token) {
      console.error("No token found");
      return;
    }
    
    console.log("Token retrieved:", token);

    // Authorization headers
    const config = {
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}` 
      }
    };

    console.log("Authorization headers set");

    // Making API request
    const res = await axios.post('http://localhost:4000/cart/addtocart', {
      itemId: product._id
    }, config);
    
    console.log("API response:", res);

    // Dispatch success action
    dispatch({
      type: ADD_TO_CART,
      payload: res.data
    });

    console.log("Product added to cart");

  } catch (error) {
    console.error("Error adding to cart:", error.response?.data || error.message);

    dispatch({
      type: CART_ERROR,
      payload: error.response?.data || 'An error occurred'
    });
  }
};

// Get cart items
export const getCart = () => async (dispatch, getState) => {
  try {
    console.log("getcart started");

    const { token } = getState().user;
    if (!token) {
      console.error("No token found");
      return;
    }

    console.log("Token retrieved:", token);

    const config = {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    };

    console.log("Authorization headers set");

    const res = await axios.get('http://localhost:4000/cart/getcart', config);
    
    console.log("API response:", res);

    dispatch({
      type: GET_CART,
      payload: res.data
    });

    console.log("Cart items fetched");

  } catch (error) {
    console.error("Error getting cart items:", error.response?.data || error.message);

    dispatch({
      type: CART_ERROR,
      payload: error.response?.data || 'An error occurred'
    });
  }
};


export const removeFromCart = (itemId) => async dispatch => {
  try {
    const res = await axios.delete(`${API_URL}/removefromcart`, { data: { itemId } });
    dispatch({
      type: REMOVE_FROM_CART,
      payload: itemId
    });
  } catch (error) {
    dispatch({
      type: CART_ERROR,
      payload: error.response.data
    });
  }
};
