// src/reducers/productReducer.js
const initialState = {
    products: [],
    // Add other product-related states if needed
  };
  
  const productReducer = (state = initialState, action) => {
    switch (action.type) {
      case 'SET_PRODUCTS':
        // Handle setting products
        return { ...state, products: action.payload };
      default:
        return state;
    }
  };
  
  export default productReducer;
