import {
  ADD_TO_CART_SUCCESS,
  REMOVE_FROM_CART_SUCCESS,
  UPDATE_CART_SUCCESS,
  GET_CART_REQUEST,
  GET_CART_SUCCESS,
  GET_CART_FAIL,
  CART_ERROR,
} from "../actions/cartActions";

const initialState = {
  items: [], // List of items in the cart
  loading: false, // Loading state for asynchronous actions
  error: null, // Error state for handling errors
};

const cartReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_CART_REQUEST:
      return { ...state, loading: true };

    case GET_CART_SUCCESS:
      // Ensure payload is an array of items
      return { ...state, loading: false, items: action.payload, error: null };

    case GET_CART_FAIL:
      return { ...state, loading: false, error: action.payload };

    case ADD_TO_CART_SUCCESS:
      // If payload contains the updated cart, use it; otherwise, add the new item
      return {
        ...state,
        items: action.payload, // Use the updated cart
        error: null,
      };

    case UPDATE_CART_SUCCESS:
      // Update specific item in the cart
      return {
        ...state,
        loading: false,
        items: state.items.map((item) =>
          item._id === action.payload._id ? action.payload : item
        ),
        error: null,
      };

    case REMOVE_FROM_CART_SUCCESS:
      // Remove the item with the matching _id
      return {
        ...state,
        loading: false,
        items: state.items.filter((item) => item._id !== action.payload._id),
        error: null,
      };

    case CART_ERROR:
      return {
        ...state,
        error: action.payload,
      };

    default:
      return state;
  }
};

export default cartReducer;
