import axios from 'axios';

// Add to Cart
export const addToCart = (itemId) => async (dispatch, getState) => {
  try {
    await axios.post('/addtocart', { itemId });
    dispatch({ type: 'ADD_TO_CART', payload: itemId });
  } catch (error) {
    console.error('Error adding to cart', error);
  }
};

// Remove from Cart
export const removeFromCart = (itemId) => async (dispatch, getState) => {
  try {
    await axios.post('/removefromcart', { itemId });
    dispatch({ type: 'REMOVE_FROM_CART', payload: itemId });
  } catch (error) {
    console.error('Error removing from cart', error);
  }
};

// Get Cart Data
export const getCartData = () => async (dispatch) => {
  try {
    const { data } = await axios.post('/getcart');
    dispatch({ type: 'SET_CART', payload: data });
  } catch (error) {
    console.error('Error fetching cart data', error);
  }
};

// Admin Add Product
export const addProduct = (product) => async (dispatch) => {
  try {
    const { data } = await axios.post('/addproduct', product);
    dispatch({ type: 'ADD_PRODUCT', payload: data });
  } catch (error) {
    console.error('Error adding product', error);
  }
};

// Admin Remove Product
export const removeProduct = (productId) => async (dispatch) => {
  try {
    await axios.post('/removeproduct', { id: productId });
    dispatch({ type: 'REMOVE_PRODUCT', payload: productId });
  } catch (error) {
    console.error('Error removing product', error);
  }
};
