import React from 'react';
import { useSelector } from 'react-redux'; 
import { useParams } from 'react-router-dom';
import Breadcrum from '../Components/Breadcrums/Breadcrum';
import ProductDisplay from '../Components/ProductDisplay/ProductDisplay';
import DescriptionBox from '../Components/DescriptionBox/DescriptionBox';
import RelatedProducts from '../Components/RelatedProducts/RelatedProducts';

const Product = () => {
  const { productId } = useParams();
  const state = useSelector((state) => state.product || { products: [], loading: false, error: null });
  // In your Product component
console.log('Redux State:', useSelector((state) => state.product));

  const { products, loading, error } = state;

    // Debugging output
  console.log('Products:', products);
  console.log('Product ID:', productId);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  const product = products.find((e) => e._id === productId);
  console.log('Found Product:', product);

  if (!product) return <div>Product not found</div>;

  return (
    <div>
      <Breadcrum product={product} />
      <ProductDisplay product={product} />
      <DescriptionBox product={product} />
      <RelatedProducts productId={productId} />
    </div>
  );
};

export default Product;
