// export default ProductDisplay;
const ProductDisplay = (props) => {
  const { product } = props;
  const { addToCart } = useContext(ShopContext);

  if (!product) return <p>Loading...</p>; // Gracefully handle undefined product

  return (
    <div className='productdisplay'>
      <div className="productdisplay-left">
        <div className="productdisplay-img-list">
          {product.images && product.images.length > 0 ? product.images.map((image, index) => (
            <img key={index} src={image} alt={`Product image ${index + 1}`} />
          )) : <p>No images available</p>}
        </div>
        <div className="productdisplay-img">
          {product.images && product.images.length > 0 ? (
            <img className='productdisplay-main-img' src={product.images[0]} alt="Main product view" />
          ) : (
            <p>No main image available</p>
          )}
        </div>
      </div>
      <div className="productdisplay-right">
        <h1>{product.name || 'Product Name'}</h1>
        <div className="productdisplay-right-stars">
          {[...Array(5)].map((_, index) => (
            <img
              key={index}
              src={index < (product.rating || 0) ? star_icon : star_dull_icon} // Replace with dynamic rating if available
              alt="Rating star"
            />
          ))}
          <p>({product.reviewsCount || 0})</p>
        </div>
        <div className="productdisplay-right-prices">
          {product.old_price && (
            <div className="productdisplay-right-price-old">${product.old_price}</div>
          )}
          <div className="productdisplay-right-price-new">${product.new_price}</div>
        </div>
        <div className="productdisplay-right-description">
          {product.description || 'Description not available.'}
        </div>
        <div className="productdisplay-right-size">
          <h1>Select Size</h1>
          <div className="productdisplay-right-sizes">
            {product.sizes && product.sizes.length > 0 ? product.sizes.map((size, index) => (
              <div key={index}>{size}</div>
            )) : <p>No sizes available</p>}
          </div>
        </div>
        <button onClick={() => addToCart(product._id)}>ADD TO CART</button>
        <p className='productdisplay-right-category'>
          <span>Category :</span> {product.category || 'Category not available'}
        </p>
        <p className='productdisplay-right-tags'>
          <span>Tags :</span> {product.tags && product.tags.length > 0 ? product.tags.join(', ') : 'No tags'}
        </p>
      </div>
    </div>
  );
};
