import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchNewCollections } from '../../redux/actions/newCollectionsActions';
import './NewCollections.css';
import Item from '../Item/Item';

const NewCollections = () => {
  const dispatch = useDispatch();
  const { newCollections = [], loading, error } = useSelector(state => state.newCollections || {});

  useEffect(() => {
    dispatch(fetchNewCollections());
  }, [dispatch]);

  return (
    <div className='new-collections'>
      <h1>NEW COLLECTIONS</h1>
      <hr />
      {loading ? (
        <p>Loading...</p>
      ) : error ? (
        <p>Error: {error}</p>
      ) : (
        <div className="collections">
         {newCollections.map((item, i) => (
  <Item
    key={i}
    id={item._id}
    name={item.name}
    image={`http://localhost:4000/newcollection${item.image}`} // Ensure this is correct
    new_price={item.new_price}
    old_price={item.old_price}
  />
))}

        </div>
      )}
    </div>
  );
};

export default NewCollections;
