import React, { useState } from 'react';
import axios from 'axios';
import { useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import ReCAPTCHA from 'react-google-recaptcha';
import {
  login,
  signup,
  forgotPasswordSuccess,
  forgotPasswordFail
} from '../redux/actions/authActions';
import './CSS/LoginSignup.css';

const LoginSignup = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });
  const [captchaToken, setCaptchaToken] = useState('');
  const [error, setError] = useState('');
  const [state, setState] = useState('Login');
  const navigate = useNavigate();
  const dispatch = useDispatch();

  const changeHandler = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleCaptcha = (value) => {
    setCaptchaToken(value);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!formData.email || (state !== 'Forgot Password' && !formData.password)) {
      setError('Please fill all fields');
      return;
    }

    if (!captchaToken) {
      setError('Please complete the CAPTCHA');
      return;
    }

    const userData = {
      ...formData,
      captchaToken
    };

    try {
      switch (state) {
        case 'Forgot Password':
          // Since there's no forgotPassword action, we'll use axios directly here
          const response = await axios.post('/auth/forgot-password', userData);
          dispatch(forgotPasswordSuccess(response.data.message));
          alert('Password reset email sent if the email exists.');
          break;
        case 'Login':
          dispatch(login(userData));
          break;
        case 'Sign Up':
          dispatch(signup(userData));
          break;
        default:
          setError('Invalid state');
          return;
      }

      // Navigation should be handled in a useEffect hook that watches for authentication state
    } catch (err) {
      let errorMsg = 'An unexpected error occurred';
      if (err.response && err.response.data && err.response.data.errors) {
        errorMsg = err.response.data.errors;
      }
      setError(errorMsg);
      if (state === 'Forgot Password') {
        dispatch(forgotPasswordFail(errorMsg));
      }
      // For login and signup, errors are already handled in the action creators
    }
  };





  

  return (
    <div className="loginsignup">
      <div className="loginsignup-container">
        <h1>{state}</h1>
        <form onSubmit={handleSubmit}>
          <div className="loginsignup-fields">
            {state === 'Sign Up' && (
              <input
                type="text"
                placeholder="Your Name"
                name="username"
                value={formData.username}
                onChange={changeHandler}
              />
            )}
            <input
              type="email"
              placeholder="Email Address"
              name="email"
              value={formData.email}
              onChange={changeHandler}
            />
            {state !== 'Forgot Password' && (
              <input
                type="password"
                placeholder="Password"
                name="password"
                value={formData.password}
                onChange={changeHandler}
              />
            )}
          </div>

          <ReCAPTCHA
            sitekey={process.env.REACT_APP_RECAPTCHA_SITE_KEY} // Use environment variable for site key
            onChange={handleCaptcha}
          />

          <div className="loginsignup-agree">
            <input type="checkbox" name="agree" id="agree" />
            <p>By continuing, I agree to the terms of use & privacy policy.</p>
          </div>
          <button type="submit">Continue</button>

          {state === 'Login' ? (
            <>
              <p className="loginsignup-login">
                Don't have an account? <span onClick={() => setState('Sign Up')}>Sign up here</span>
              </p>
              <p className="loginsignup-login">
                Forgot your password? <span onClick={() => setState('Forgot Password')}>Click here</span>
              </p>
            </>
          ) : state === 'Sign Up' ? (
            <p className="loginsignup-login">
              Already have an account? <span onClick={() => setState('Login')}>Login here</span>
            </p>
          ) : (
            <p className="loginsignup-login">
              Remembered your password? <span onClick={() => setState('Login')}>Login here</span>
            </p>
          )}

          {error && <p className="loginsignup-error">{error}</p>}
        </form>
      </div>
    </div>
  );
};

export default LoginSignup;
