// export default LoginSignup
import React, { useState } from 'react';
import axios from 'axios';
import ReCAPTCHA from 'react-google-recaptcha'; // For reCAPTCHA
import './CSS/LoginSignup.css';

const LoginSignup = () => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: ''
  });
  const [captchaToken, setCaptchaToken] = useState('');
  const [error, setError] = useState('');
  const [state, setState] = useState('Login'); // States: 'Login', 'Sign Up', 'Forgot Password'

  const changeHandler = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleCaptcha = (value) => {
    setCaptchaToken(value);
  };

  const handleSubmit = async () => {
    if (!formData.email || (state !== 'Forgot Password' && !formData.password)) {
      setError('Please fill all fields');
      return;
    }

    if (!captchaToken) {
      setError('Please complete the CAPTCHA');
      return;
    }

    try {
      let endpoint;
      if (state === 'Forgot Password') {
        endpoint = '/auth/forgot-password';
        await axios.post(endpoint, {
          email: formData.email,
          captchaToken
        });
        alert('Password reset email sent if the email exists.');
      } else {
        endpoint = state === 'Login' ? '/auth/login' : '/auth/signup';
        const response = await axios.post(endpoint, {
          ...formData,
          captchaToken
        });
        // Handle success (e.g., store token, redirect)
        console.log(response.data);
      }
    } catch (err) {
      setError(err.response?.data?.errors || 'An error occurred');
    }
  };

  return (
    <div className="loginsignup">
      <div className="loginsignup-container">
        <h1>{state}</h1>
        <form onSubmit={(e) => { e.preventDefault(); handleSubmit(); }}>
          <div className="loginsignup-fields">
            {state === "Sign Up" && (
              <input
                type="text"
                placeholder="Your Name"
                name="username"
                value={formData.username}
                onChange={changeHandler}
              />
            )}
            <input
              type="email"
              placeholder="Email Address"
              name="email"
              value={formData.email}
              onChange={changeHandler}
            />
            {state !== "Forgot Password" && (
              <input
                type="password"
                placeholder="Password"
                name="password"
                value={formData.password}
                onChange={changeHandler}
              />
            )}
          </div>

          <ReCAPTCHA
            sitekey="your_site_ke" // Replace with your reCAPTCHA site key
            onChange={handleCaptcha}
          />

          <button type="submit">Continue</button>

          {state === "Login" ? (
            <>
              <p className="loginsignup-login">
                Don't have an account? <span onClick={() => setState("Sign Up")}>Sign up here</span>
              </p>
              <p className="loginsignup-login">
                Forgot your password? <span onClick={() => setState("Forgot Password")}>Click here</span>
              </p>
            </>
          ) : state === "Sign Up" ? (
            <p className="loginsignup-login">
              Already have an account? <span onClick={() => setState("Login")}>Login here</span>
            </p>
          ) : (
            <p className="loginsignup-login">
              Remembered your password? <span onClick={() => setState("Login")}>Login here</span>
            </p>
          )}

          <div className="loginsignup-agree">
            <input type="checkbox" name="agree" id="agree" />
            <p>By continuing, I agree to the terms of use & privacy policy.</p>
          </div>

          {error && <p className="loginsignup-error">{error}</p>}
        </form>
      </div>
    </div>
  );
};

export default LoginSignup;
