import React, { useEffect } from "react";
import "./CartItems.css";
import cross_icon from "../Assets/cart_cross_icon.png";
import { useSelector, useDispatch } from "react-redux";
import { fetchCartItems, removeFromCart, updateCartQuantity } from "../../redux/actions/cartActions";
import { backend_url, currency } from "../../App";
import jwt_decode from "jwt-decode";

const CartItems = () => {
  const dispatch = useDispatch();
  const { cartItems } = useSelector((state) => state.cart);
  
  // Get token from localStorage and decode it to get userId
  const token = localStorage.getItem('token');
  let userId;
  if (token) {
    const decoded = jwt_decode(token);
    userId = decoded.user.id; // or decoded.id based on your JWT structure
  }

  useEffect(() => {
    if (userId) {
      dispatch(fetchCartItems(userId));
    }
  }, [dispatch, userId]);

  const getTotalCartAmount = () => {
    return cartItems.reduce((total, item) => total + item.new_price * item.quantity, 0);
  };

  const handleRemove = (productId) => {
    if (userId) {
      dispatch(removeFromCart(userId, productId));
    }
  };

  const handleQuantityChange = (productId, newQuantity) => {
    if (userId) {
      dispatch(updateCartQuantity(userId, productId, newQuantity));
    }
  };

  return (
    <div className="cartitems">
      <div className="cartitems-format-main">
        <p>Products</p>
        <p>Title</p>
        <p>Price</p>
        <p>Quantity</p>
        <p>Total</p>
        <p>Remove</p>
      </div>
      <hr />
      {cartItems.map((e) => (
        <div key={e.productId}>
          <div className="cartitems-format-main cartitems-format">
            <img className="cartitems-product-icon" src={backend_url + e.image} alt={e.name} />
            <p className="cartitems-product-title">{e.name}</p>
            <p>{currency}{e.new_price}</p>
            <input 
              className="cartitems-quantity" 
              type="number" 
              value={e.quantity} 
              onChange={(event) => handleQuantityChange(e.productId, Number(event.target.value))}
            />
            <p>{currency}{e.new_price * e.quantity}</p>
            <img 
              onClick={() => handleRemove(e.productId)} 
              className="cartitems-remove-icon" 
              src={cross_icon} 
              alt="Remove" 
            />
          </div>
          <hr />
        </div>
      ))}
      
      <div className="cartitems-down">
        <div className="cartitems-total">
          <h1>Cart Totals</h1>
          <div>
            <div className="cartitems-total-item">
              <p>Subtotal</p>
              <p>{currency}{getTotalCartAmount()}</p>
            </div>
            <hr />
            <div className="cartitems-total-item">
              <p>Shipping Fee</p>
              <p>Free</p>
            </div>
            <hr />
            <div className="cartitems-total-item">
              <h3>Total</h3>
              <h3>{currency}{getTotalCartAmount()}</h3>
            </div>
          </div>
          <button>PROCEED TO CHECKOUT</button>
        </div>
        <div className="cartitems-promocode">
          <p>If you have a promo code, Enter it here</p>
          <div className="cartitems-promobox">
            <input type="text" placeholder="promo code" />
            <button>Submit</button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CartItems;
