import axios from 'axios';

export const fetchAllProducts = () => async (dispatch) => {
  try {
    const response = await axios.get('http://localhost:4000/allproducts');
    dispatch({ type: 'FETCH_PRODUCTS_SUCCESS', payload: response.data });
  } catch (error) {
    dispatch({ type: 'FETCH_PRODUCTS_FAILURE', payload: error.message });
  }
};

export const fetchProductsByCategory = (category) => async (dispatch) => {
  try {
    const response = await axios.get(`http://localhost:4000/allproducts/${category}`);
    dispatch({ type: 'FETCH_PRODUCTS_BY_CATEGORY_SUCCESS', payload: response.data });
  } catch (error) {
    dispatch({ type: 'FETCH_PRODUCTS_BY_CATEGORY_FAILURE', payload: error.message });
  }
};
