import React from 'react';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Home from './Pages/Home';
import ShopCategory from './Pages/ShopCategory';
import Product from './Pages/Product';
import Cart from './Pages/Cart';
import LoginSignup from './Pages/LoginSignup';
import Footer from './Components/Footer/Footer';
import Navbar from './Components/Navbar/Navbar';
import PrivateRoute from './Components/PrivateRoute'; // Import PrivateRoute


import plant_banner from './Components/Assets/banner_plants.png';
import pots_banner from './Components/Assets/banner_pots.png';
import tools_banner from './Components/Assets/banner_tools.png';
import gift_banner from './Components/Assets/banner_gifts.png';
export const backend_url = 'http://localhost:4000';
export const currency = '₹';

function App() {
  const dispatch = useDispatch();

  useEffect(() => {
    const appRestarted = localStorage.getItem('app_restarted');

    if (appRestarted) {
      localStorage.removeItem('token');
      localStorage.removeItem('app_restarted');
      dispatch(logout());
    }
  }, [dispatch]);

  return (
    <BrowserRouter>
      <Navbar />
      <Routes>
        <Route path="/login" element={<LoginSignup />} />
        <Route path="/" element={<PrivateRoute element={<Home />} />} />
        <Route path="/plants" element={<PrivateRoute element={<ShopCategory banner={plant_banner} category="plants" />} />} />
        <Route path="/pots" element={<PrivateRoute element={<ShopCategory banner={pots_banner} category="pots" />} />} />
        <Route path="/tools" element={<PrivateRoute element={<ShopCategory banner={tools_banner} category="tools" />} />} />
        <Route path="/gifts" element={<PrivateRoute element={<ShopCategory banner={gift_banner} category="gifts" />} />} />
        <Route path="/product/:productId" element={<PrivateRoute element={<Product />} />} />
        <Route path="/cart" element={<PrivateRoute element={<Cart />} />} />
      </Routes>
      <Footer />
    </BrowserRouter>
  );
}

export default App;
