import React, { useContext } from 'react';
import { ShopContext } from '../../Context/ShopContext';
import './ProductDisplay.css';
import star_icon from '../Assets/star_icon.png'; // Ensure you have these assets
import star_dull_icon from '../Assets/star_dull_icon.png';

const ProductDisplay = (props) => {
  const { productId } = props.match.params; // Assuming you're using react-router
  const { products, addToCart } = useContext(ShopContext);

  if (!products || !Array.isArray(products)) {
    return <div>Loading...</div>; // Placeholder for loading state
  }

  const product = products.find(p => p._id === productId);

  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div className='productdisplay'>
      <div className="productdisplay-left">
        <div className="productdisplay-img-list">
          {/* Assuming you have multiple images */}
          {product.images.map((img, index) => (
            <img key={index} src={img} alt={`Product ${index + 1}`} />
          ))}
        </div>
        <div className="productdisplay-img">
          <img className='productdisplay-main-img' src={product.image} alt={product.name} />
        </div>
      </div>
      <div className="productdisplay-right">
        <h1>{product.name}</h1>
        <div className="productdisplay-right-stars">
          {/* Example of 4 stars filled and 1 star empty */}
          {[...Array(5)].map((_, index) => (
            <img 
              key={index} 
              src={index < product.rating ? star_icon : star_dull_icon} 
              alt="star" 
            />
          ))}
          <p>({product.reviews} reviews)</p>
        </div>
        <div className="productdisplay-right-prices">
          <div className="productdisplay-right-price-old">₹{product.old_price}</div>
          <div className="productdisplay-right-price-new">₹{product.new_price}</div>
        </div>
        <div className="productdisplay-right-description">
          {product.description}
        </div>
        <div className="productdisplay-right-details">
          <p>Shipping: ₹{product.shipping_cost} for entire order</p>
          <p>Dispatch in {product.dispatch_time} days</p>
          <p>Country of origin: {product.country_of_origin}</p>
        </div>
        <div className="productdisplay-right-size">
          <h1>Select Quantity</h1>
          <input type="number" min="1" max="99" defaultValue="1" />
        </div>
        <button onClick={() => addToCart(product._id)}>ADD TO CART</button>
        <p className='productdisplay-right-category'><span>Category :</span> {product.category}</p>
        <p className='productdisplay-right-category'><span>Tags :</span> {product.tags.join(', ')}</p>
      </div>
    </div>
  );
}

export default ProductDisplay;
