// Example of an action to add to the cart\


export const addToCart = (productId) => async (dispatch, getState) => {
  try {
    const { token } = getState().auth?.user || {}; // Get token from auth state

    if (!token) {
      throw new Error('User not authenticated');
    }

    const config = {
      headers: {
        'Content-Type': 'application/json',
        Authorization: `Bearer ${token}`
      }
    };

    const { data } = await axios.post(`/api/cart`, { productId }, config);

    dispatch({
      type: 'ADD_TO_CART_SUCCESS',
      payload: data
    });
  } catch (error) {
    console.error('Error adding to cart:', error.message);
    dispatch({
      type: 'ADD_TO_CART_FAIL',
      payload: error.message
    });
  }
};


// Get cart items
export const getCart = () => async (dispatch, getState) => {
  try {
    console.log("getcart started");

    const { token } = getState().user;
    if (!token) {
      console.error("No token found");
      return;
    }

    console.log("Token retrieved:", token);

    const config = {
      headers: {
        'Authorization': `Bearer ${token}`
      }
    };

    console.log("Authorization headers set");

    const res = await axios.get('http://localhost:4000/cart/getcart', config);
    
    console.log("API response:", res);

    dispatch({
      type: GET_CART,
      payload: res.data
    });

    console.log("Cart items fetched");

  } catch (error) {
    console.error("Error getting cart items:", error.response?.data || error.message);

    dispatch({
      type: CART_ERROR,
      payload: error.response?.data || 'An error occurred'
    });
  }
};


export const removeFromCart = (itemId) => async dispatch => {
  try {
    const res = await axios.delete(`${API_URL}/removefromcart`, { data: { itemId } });
    dispatch({
      type: REMOVE_FROM_CART,
      payload: itemId
    });
  } catch (error) {
    dispatch({
      type: CART_ERROR,
      payload: error.response.data
    });
  }
};
