// redux/slices/themeSlice.js
const initialState = {
    theme: 'light', // Default theme
  };
  
  // Actions
  const SET_THEME = 'SET_THEME';
  
  export const setTheme = (theme) => ({
    type: SET_THEME,
    payload: theme,
  });
  
  // Reducer
