import React, { useState } from 'react';
import { useDispatch } from 'react-redux';
import { addToCart } from '../../redux/actions/cartActions';
import './ProductDisplay.css';

const ProductDisplay = ({ product }) => {
  const dispatch = useDispatch();
  const [isAdding, setIsAdding] = useState(false);

  // const handleAddToCart = async () => {
  //   if (!product || !product._id) {
  //     console.error('Invalid product:', product);
  //     return;
  //   }

  //   setIsAdding(true);
  //   try {
  //     await dispatch(addToCart(product._id));
  //     // Optionally, you can show a success message here
  //   } catch (error) {
  //     console.error('Error adding to cart:', error);
  //     // Optionally, you can show an error message here
  //   } finally {
  //     setIsAdding(false);
  //   }
  // };

  const handleAddToCart = (itemId) => {
    const item = cartItems.find(item => item._id === itemId);
    
    if (item) {
      // Item is already in the cart, increase quantity
      const newQuantity = item.quantity + 1;
      
      // Optimistically update the quantity in the UI
      dispatch({ type: 'OPTIMISTIC_UPDATE_QUANTITY', payload: { itemId, quantity: newQuantity } });
  
      // Call the debounced function to update quantity in the backend
      debouncedUpdateQuantity(itemId, newQuantity);
  
    } else {
      // Item is not in the cart, add it with an initial quantity of 1
      const newCartItem = { _id: itemId, quantity: 1 };
      
      // Optimistically add the item to the cart in the UI
      dispatch({ type: 'OPTIMISTIC_ADD_TO_CART', payload: newCartItem });
  
      // Call the backend to actually add the item to the cart
      addToCart(itemId);
    }
  };
  

  if (!product) {
    return <div>Product not found</div>;
  }

  const imageUrl = product.image.startsWith('/') ? `http://localhost:4000${product.image}` : product.image;

  return (
    <div className='productdisplay'>
      <div className="productdisplay-left">
        <div className="productdisplay-img-list">
          {product.images && product.images.map((img, index) => (
            <img
              key={index}
              src={img.startsWith('/') ? `http://localhost:4000${img}` : img}
              alt={`Product ${index}`}
              className='productdisplay-thumbnail'
            />
          ))}
        </div>
        <div className="productdisplay-img">
          <img className='productdisplay-main-img' src={imageUrl} alt={product.name} />
        </div>
      </div>
      <div className="productdisplay-right">
        <h1>{product.name}</h1>
        {product.category === 'plants' && (
          <div className="productdisplay-right-plant-details">
            <p>Watering Needs: {product.wateringNeeds || 'N/A'}</p>
            <p>Sunlight: {product.sunlight || 'N/A'}</p>
          </div>
        )}
        <div className="productdisplay-right-stars">
          {Array.from({ length: 5 }, (_, index) => (
            <span
              key={index}
              className={index < product.rating ? 'star filled' : 'star'}
              aria-label={`Star rating ${index + 1}`}
            >
              ★
            </span>
          ))}
        </div>
        <div className="productdisplay-right-prices">
          <div className="productdisplay-right-price-old">₹{product.old_price.toFixed(2)}</div>
          <div className="productdisplay-right-price-new">₹{product.new_price.toFixed(2)}</div>
        </div>
        <div className="productdisplay-right-description">
          <p>{product.description}</p>
        </div>
        {product.category !== 'tools' && (
          <div className="productdisplay-right-size">
            <h1>Select Size</h1>
            <div className="productdisplay-right-sizes">
              {/* Size selection options */}
            </div>
          </div>
        )}
        <button onClick={handleAddToCart} disabled={isAdding}>
          {isAdding ? 'Adding...' : 'Add to Cart'}
        </button>
        <p className='productdisplay-right-category'>
          <span>Category :</span> {product.category}
        </p>
        <p className='productdisplay-right-tags'>
          <span>Tags :</span> Lesswater, low light
        </p>
      </div>
    </div>
  );
};

export default ProductDisplay;
