// export default cartReducer;
const initialState = {
  cartItems: [],
  loading: false,
  error: null,
};

 const cartReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD_TO_CART_SUCCESS":
    case "REMOVE_FROM_CART_SUCCESS":
    case "UPDATE_CART_SUCCESS":
      return {
        ...state,
        cartItems: action.payload, // Update the cart items directly
        loading: false,
      };
    case "GET_CART_REQUEST":
      return {
        ...state,
        loading: true,
      };
    case "GET_CART_SUCCESS":
      return {
        ...state,
        cartItems: action.payload,
        loading: false,
      };
    case "GET_CART_FAIL":
    case "CART_ERROR":
      return {
        ...state,
        loading: false,
        error: action.payload,
      };
    default:
      return state;
  }
};
