// export default Product
import React, { useContext } from 'react';
import { ShopContext } from '../Context/ShopContext';
import { useParams } from 'react-router-dom';
import Breadcrum from '../Components/Breadcrums/Breadcrum';
import ProductDisplay from '../Components/ProductDisplay/ProductDisplay';
import DescriptionBox from '../Components/DescriptionBox/DescriptionBox';
import RelatedProducts from '../Components/RelatedProducts/RelatedProducts';

const Product = () => {
  const { products } = useContext(ShopContext); // Ensure the context has the products
  const { productId } = useParams();

  // Find the product based on ID from the URL parameters
  const product = products.find((e) => e._id === productId);

  // If the product is not found, you might want to handle this case
  if (!product) {
    return <div>Product not found</div>;
  }

  return (
    <div>
      <Breadcrum product={product} />
      <ProductDisplay product={product} />
      <DescriptionBox product={product} /> {/* Pass product if needed */}
      <RelatedProducts productId={productId} /> {/* Pass productId if needed */}
    </div>
  );
};

export default Product;
