// authActions.js

import axios from 'axios';

// Action creator for login
export const login = (userData) => async (dispatch) => {
  try {
    const response = await axios.post(`${process.env.REACT_APP_API_URL}/auth/login`, userData);
    const { token } = response.data;

    // Store the token in local storage or Redux store
    localStorage.setItem('token', token);
    dispatch({ type: 'LOGIN_SUCCESS', payload: token });
  } catch (error) {
    console.error('Login error:', error.response ? error.response.data : error.message);
    dispatch({ type: 'LOGIN_FAIL', payload: error.response ? error.response.data : error.message });
  }
};

export const signup = (userData) => async (dispatch) => {
    try {
      const response = await axios.post(`${process.env.REACT_APP_API_URL}/auth/signup`, userData);
      const { token } = response.data;
  
      // Store the token in local storage or Redux store
      localStorage.setItem('token', token);
      dispatch({ type: 'SIGNUP_SUCCESS', payload: token });
    } catch (error) {
      console.error('Signup error:', error.response ? error.response.data : error.message);
      dispatch({ type: 'SIGNUP_FAIL', payload: error.response ? error.response.data : error.message });
    }
  };
  
  // Action creator for forgot password
  export const forgotPassword = (userData) => async (dispatch) => {
    try {
      await axios.post(`${process.env.REACT_APP_API_URL}/auth/forgot-password`, userData);
      dispatch({ type: 'FORGOT_PASSWORD_SUCCESS' });
    } catch (error) {
      console.error('Forgot password error:', error.response ? error.response.data : error.message);
      dispatch({ type: 'FORGOT_PASSWORD_FAIL', payload: error.response ? error.response.data : error.message });
    }
  };
