// authActions.js

import axios from 'axios';

// Action creator for login
export const login = (userData) => async (dispatch) => {
  try {
    const response = await axios.post(`${process.env.REACT_APP_API_URL}/auth/login`, userData);
    const { token } = response.data;

    // Store the token in local storage or Redux store
    localStorage.setItem('token', token);
    dispatch({ type: 'LOGIN_SUCCESS', payload: token });
  } catch (error) {
    console.error('Login error:', error.response ? error.response.data : error.message);
    dispatch({ type: 'LOGIN_FAIL', payload: error.response ? error.response.data : error.message });
  }
};
