// export default cartReducer;
import {
  ADD_TO_CART_SUCCESS,
  ADD_TO_CART_FAIL,
  REMOVE_FROM_CART_SUCCESS,
  REMOVE_FROM_CART_FAIL,
  UPDATE_CART_SUCCESS,
  UPDATE_CART_FAIL,
  GET_CART_REQUEST,
  GET_CART_SUCCESS,
  GET_CART_FAIL,
  CART_ERROR,
} from "../../redux/actions/";

const initialState = {
  items: [],  // Make sure to initialize with an empty array or your expected initial state
  loading: false,
  error: null,
};

const cartReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_CART_REQUEST:
      return { ...state, loading: true };

    case GET_CART_SUCCESS:
      return { ...state, loading: false, items: action.payload, error: null };

    case GET_CART_FAIL:
      return { ...state, loading: false, error: action.payload };

    case ADD_TO_CART_SUCCESS:
      return {
        ...state,
        items: [...state.items, action.payload], // Add new item to cart
        error: null,
      };

    case ADD_TO_CART_FAIL:
      return {
        ...state,
        error: action.payload,
      };

    case UPDATE_CART_SUCCESS:
      return {
        ...state,
        loading: false,
        items: state.items.map(item =>
          item._id === action.payload._id ? action.payload : item
        ),
        error: null,
      };

    case UPDATE_CART_FAIL:
      return {
        ...state,
        error: action.payload,
      };

    case REMOVE_FROM_CART_SUCCESS:
      return {
        ...state,
        loading: false,
        items: state.items.filter(item => item._id !== action.payload._id), // Remove item from cart
        error: null,
      };

    case REMOVE_FROM_CART_FAIL:
      return {
        ...state,
        error: action.payload,
      };

    case CART_ERROR:
      return {
        ...state,
        error: action.payload,
      };

    default:
      return state;
  }
};

export default cartReducer;
