import './App.css';
import Navbar from './Components/Navbar/Navbar';
import { BrowserRouter, Routes, Route, Navigate } from 'react-router-dom';
import Home from './Pages/Home';
import ShopCategory from './Pages/ShopCategory';
import Product from './Pages/Product';
import Cart from './Pages/Cart';
import LoginSignup from './Pages/LoginSignup';
import Footer from './Components/Footer/Footer';
import { useSelector } from 'react-redux';
import plant_banner from './Components/Assets/banner_plants.png';
import pots_banner from './Components/Assets/banner_pots.png';
import tools_banner from './Components/Assets/banner_tools.png';
import gift_banner from './Components/Assets/banner_gifts.png';

export const backend_url = 'http://localhost:4000';
export const currency = '₹';

function App() {
  const isAuthenticated = useSelector((state) => state.auth.isAuthenticated);

  return (
    <div>
      <BrowserRouter>
        {isAuthenticated && <Navbar />}
        <Routes>
          <Route path='/login' element={!isAuthenticated ? <LoginSignup /> : <Navigate to="/" />} />
          {isAuthenticated ? (
            <>
              <Route path='/' element={<Home />} />
              <Route path='/plants' element={<ShopCategory banner={plant_banner} category="plants" />} />
              <Route path='/pots' element={<ShopCategory banner={pots_banner} category="pots" />} />
              <Route path='/tools' element={<ShopCategory banner={tools_banner} category="tools" />} />
              <Route path='/gifts' element={<ShopCategory banner={gift_banner} category="gifts" />} />
              <Route path="/product" element={<Product />}>
                <Route path=':productId' element={<Product />} />
              </Route>
              <Route path='/cart' element={<Cart />} />
            </>
          ) : (
            <Route path='/*' element={<Navigate to="/login" />} />
          )}
        </Routes>
        {isAuthenticated && <Footer />}
      </BrowserRouter>
    </div>
  );
}

export default App;
